let queryTemplate = "{\"attribute\":{\"key\":\"{key}\",\"id\":\"\",\"isCustomerData\":false},\"operator\":\"Equals\",\"value\":{\"items\":[\"{value}\"]}}";
let fullQuery = "{\"conditionSet\":{\"operator\":\"And\",\"conditionSets\":[],\"conditions\":[{conditions}]}}";
let conditions = "";
if (request_vendor_parameters) {
	console.log(request_vendor_parameters);
	let expressions = request_vendor_parameters;
	let keys = Object.keys(expressions);	
	let i = 0;
	for (let key of keys) {
		let query = queryTemplate.replace("{key}", key);
		query = query.replace("{value}", expressions[key]);
		if (i + 1 < keys.length) {
			query += ","
		}
		conditions += query;
	}
	fullQuery = fullQuery.replace("{conditions}", conditions);
	done({
		"request_vendor_body": fullQuery
	});
} else {
	done();
}

var key = "Bearer "+ configuration['requestToken'];
request_vendor_headers["Authorization"] = key;	

done({		
	"request_vendor_headers": request_vendor_headers		
});
