let body = JSON.parse(request_body);
let newPath = request_vendor_path;
let supportedActions = ["archive", "read", "rename", "purpose", "topic", "unarchive", "leave", "join", "open", "close"];

if (supportedActions.indexOf(body.action) >= 0) {
	let actionType = body.action;
	switch(actionType){
		case "read":
			actionType = "mark";
			break;
		case "purpose":
			actionType = "setPurpose";
			break;
		case "topic":
			actionType = "setTopic";
			break;
	}
	newPath = newPath + actionType;
	body.action = null;
}

done ({
	"request_vendor_body": JSON.stringify(body), 
	"request_vendor_path": newPath,
	"continue": true 
});

let body = JSON.parse(request_body);
let newPath = request_vendor_path;
let supportedActions = ["revoke", "share"];

if (supportedActions.indexOf(body.action) >= 0) {
	let actionType = body.action;
	newPath = newPath + actionType;
	body.action = null;
}

done ({
	"request_vendor_body": JSON.stringify(body), 
	"request_vendor_path": newPath,
	"continue": true 
});

let body = JSON.parse(request_body);
let newPath = request_vendor_path;
let supportedActions = ["enable", "disable"];

if (supportedActions.indexOf(body.action) >= 0) {
	let actionType = body.action;
	newPath = newPath + actionType;
	body.action = null;
}

done ({
	"request_vendor_body": JSON.stringify(body), 
	"request_vendor_path": newPath,
	"continue": true 
});

let vendor_path = request_vendor_path;
let vendor_params = request_vendor_parameters;

if (request_parameters.private === "true" && request_vendor_path.indexOf("channels") >= 0){
	// change 'channels' to 'groups'
	vendor_path = request_vendor_path.replace("channels", "groups");
} else if (request_parameters.group === "true" && request_path.indexOf("messages") >= 0 && request_vendor_path.indexOf("im") >= 0){
	// change 'channels' to 'groups'
	vendor_path = request_vendor_path.replace("im", "mpim");
}

done({
	"continue": true,
	"request_vendor_path": vendor_path,
	"request_vendor_parameters": vendor_params
});

if (response_body.group || response_body.groups) {
	let new_body = {};
	for (let key in response_body){
		if (key === "groups") {
			new_body.channels = response_body[key];
		} else if (key === "group") {
			new_body.channel = response_body[key];
		} else {
			new_body[key] = response_body[key];
		}
	}
	done({
		"response_body": new_body,
		"continue": true
	});
} else if (response_body.ok === false && response_body.error) {
	let response = response_body.error;
	done({
		"response_status_code": 400,
		"response_body": response,
		"continue": true
	});
} else {
	done({
		"continue": true
	});
}

let formattedEvents = [];
let eventObj = {};
eventObj.event_date = events["createdDate"];

let path;

if (events.message){
	path = events.message.raw;
} else {
	path = events;
}

let webhook_types = path.event.type;

if (path.event.subtype){
	webhook_types = path.event.subtype;
}

switch(webhook_types){
	case 'channel_created':
		eventObj.event_type = 'CREATED';
		eventObj.event_object_type = 'channels';
		eventObj.event_object_id = path.event.channel;
		break;
	case 'channel_archive':
	case 'channel_unarchive':
	case 'channel_rename':
	case 'channel_purpose':
	case 'group_open':
	case 'group_archive':
	case 'group_unarchive':
		eventObj.event_type = 'UPDATED';
		eventObj.event_object_type = 'channels';
		eventObj.event_object_id = path.event.channel;
		break;
	case 'channel_deleted':
		eventObj.event_type = 'DELETED';
		eventObj.event_object_type = 'channels';
		eventObj.event_object_id = path.event.channel;
		break;
	case 'team_join':
		eventObj.event_type = 'CREATED';
		eventObj.event_object_type = 'users';
		eventObj.event_object_id = path.event.user.id;
		break;
	case 'user_change':
		eventObj.event_type = 'UPDATED';
		eventObj.event_object_type = 'users';
		eventObj.event_object_id = path.event.user.id;
		break;
	case 'message':
		eventObj.event_type = 'CREATED';
		eventObj.event_object_type = 'messages';
		eventObj.event_channel_id = path.event.channel;
		eventObj.event_object_id = path.event.event_ts;
		break;
	case 'reaction_added':
		eventObj.event_type = 'CREATED';
		eventObj.event_object_type = 'reactions';
		eventObj.event_object_id = path.event.event_ts;
		break;
	case 'reaction_removed':
		eventObj.event_type = 'DELETED';
		eventObj.event_object_type = 'reactions';
		eventObj.event_object_id = path.event.event_ts;
		break;
	case 'file_created':
	case 'file_share':
	 eventObj.event_type = 'CREATED';
	 eventObj.event_object_type = 'files';
	 eventObj.event_object_id = path.event.file.id;
	 break;
	case 'file_change':
	 eventObj.event_type = 'UPDATED';
	 eventObj.event_object_type = 'files';
	 eventObj.event_object_id = path.event.file.id;
	 break;
	case 'file_deleted':
	 eventObj.event_type = 'DELETED';
	 eventObj.event_object_type = 'files';
	 eventObj.event_object_id = path.event.file.id;
	 break;
 case 'message_deleted':
 	 if (path.event.deleted_ts && path.event.file){
		 eventObj.event_type = 'DELETED';
		 eventObj.event_object_type = 'files';
		 eventObj.event_object_id = path.event.file.id;
	 }
	 break;
	default:
		eventObj.event_type = 'UNKNOWN';
		eventObj.event_object_id = path.event.event_ts;
		for (let key in path.event){
			if (path.event[key].id){
				eventObj.event_object_id = path.event[key].id;
			}
		}
}

formattedEvents.push(eventObj);

done({
	"events": formattedEvents,
	"continue": true
});
